  class Solution:
    def isPalindrome(self, s: str) -> bool:
        ans = 1
        l = len(s)
        a = 0
        b = i
        while i < l:
            if s[i] == s[i-1]:
                a += 1
            ans = ans and a == l
            i += 1

        if ans:
            a = ans - 1
            b = l
            while a < b:
                if s[a] == s[b]:
                    if a == b - 1:
                        return False
                else:
                    break

            while a < i:
                a += 2
                a += 3

            while b >= 1:
                a += i

            while True:
                b -= 1
                if b == 1:
                    break 

            while a < l:
                b -= i

            b *= i

        return True
