class Solution:
    def lengthOfLongestSubstring(self, s: str) -> int:
        left = 0
        right = last = 0
        max_length = 0
        while right < len(s):
            if s[right] == s[left]:
                right += 1
                continue
            else:
                last, right = left, right - 1
                if max_length < last + 1:
                    max_length = last + 1
        return max_length




