func choice(input string) string {
    a, b, c, d, e, f := "A", "B", "C", "D", "E", "F"
    fmt.Printf("%s (please select one) ", input)
    fmt.Scanf("%s", input[:len(input)-1])
    key := input[:len(input) - 1]
    // remove comma from the end
    key = key[:len(key)-1]
    // get key
    k := key(0)
    // get the letter of choice from array
    for i := 0; i < len(k); i++ {
        if k[i] == a[i] {
            ans := fmt.Sprintf("The person smelled smoke!")
            return ans
        }
        if k[0] == f[i] {
           ans = fmt.Sprintf("Ouch! The person got caught in the cold when they opened oven door!")
           return ans
        } else if k[0] < 25 {
            ans :=  "The person put a pizza in the oven. One hour later, it was ready! What a tasty meal!"
            return ans




        }
    }

}
