class Solution:
    def solution(self, n: int, x: List[int], m: List[int]) -> str:
        grooming = []
        for i in m:
            if n % i!= 0:
                pass
            else:
                grooming += [i]
        if len(grooming) == 1:
            grooming = grooming[0]
        else:
            grooming += grooming[1:]

        while not grooming:
            grooming.extend(x)
            grooming, x = x, []

        return grooming[0] if grooming else "tardiness"
