    def findLongestString(self, words):
        def find_max(S, w):
            if w in S:                       # 若有出现过，则返回当前有的最长结果，否则返回全局最长结果
                return max(w in S, longest)
            return S.count(w)            # 否则返回当前最长结果
        longest = 0
        for w in words:
            S = set(w)
            longest = max(S.count(w), longest)    # 找到当前最长单词
            S.add(w)                            # 累加
        return longest

