    public int[] choose(String[][] matrix) {
        int[][] dp = new int[matrix.length][matrix[0].length];
        for (int i = 0; i < matrix.length; i++) {
            for (int j = 0; j < matrix[0].length; j++) {
                if (i == 0 && j == 0 && matrix[i][j] == "A") {
                    continue;
                }
                if (matrix[i][j]!= matrix[i - 1][j] && matrix[i][0] == "A")
                    dp[i][j] = 1;
            }
        }
        return dp;
    }
}
