/**
 * @param {string[]} choices
 * @return {number[][]}
 */
let answer = (choices) => {
  if (choices.length < 2) {
    return false;
  }

  const output = new Array(choices.length);
  output[0] = [choices[0]];
  for (let i = 1; i < choices.length; i++) {
    if (choices[i] === choices[0]) {
      output[i] = output[i - 1];
      continue;
    }
  }
 return output[choices.length - 1];
};
