```javascript
module.exports = function(situation) {
  var choice = 'E';
  switch (situation.length) {
    case 0:
      return '';
    case 1:
      choice = situation.charAt(0);
      break;
    default:
      switch (situation[0]) {
        case 'A':
          choice ='movies';
          break;
        case 'B':
          choice = situation.charCodeAt(0) < 97? 'opera' : 'theater';
          break; 
        case 'C':
          choice = ((situation[0] - 64) % 64) < 30? 'lecture' : 'hall';
          break;    
        default:
          choice = 'G';            
      } 
  }
  return choice;
};
