class Solution:
        def opposite(self,s: str) -> str  :
            s = s.split(" ")
            answer = ""

            while s[len(s)-1]!= "y" :
                answer = " ".join(answer+s)
                s = self.reverse(s[:len(s)-1])

            answer = answer.capitalize()

            return answer
