public class Solution {
    public boolean canSatisfyRequirements(List<Integer> order, List<Point> foodItems, List<Requirement> revs, List<Point> available){

        Arrays.sort(available);
        for(int i = 0; i < order.size(); i++){
            Point p = foodItems.get(i);
            if(p == available.get(i))
                continue;
            Point min = available.get(i); // point with the most amount of available to the left

            while(min <= p){
                if(availale().get(min) >  order.get(i)){ // min <= p < p or p

                   if(revs.contains(new Requirement(order.get(i), min))){ // if i is bigger than available and p are equal or p then it meets the rev
                       return true;
                   }
                }
                min++;
            }
        }
       return false;
    }


    private boolean availale() {
        if(revs == null){
            return false;
        }
        for(int counter = revs.size() - 1; counter >= 0; counter--){
            Requirement r = revs.get(counter);
            if(r.getItem().intValue() <= available.size()) return true;
        }
         return false;
    }}
