    def num2str(self,num):
        strnum = ""
        tmpnum = [int(num)]
        while len(tmpnum) > 0:
            while len(strnum) < 3:
                strnum += "0"
            strnum = strnum + str(tmpnum[-1])
            del tmpnum[-1]
