            return word[0] >= 'A' and word[0] <= 'Z'

        def getPreferred(places, word) -> int:
            for i, place in enumerate(places):
                if word in place:
                    return i
            return 0

        prev = []
        for word in words:
            upperWord, lowerWord = isUpperWord(word), not isUpperWord(word)
            if upperWord in prev:
                prev.remove(upperWord)
