class Solution {
public:
    int split_pairs(string s) {
        int n = s.size();
        int count = 0;
        for (int i = 1; i < n; i++)
        {
            if (s[i-1] == s[i]) 
            {
                s.erase(s.begin()+1, s.begin()+i);
                count++;
            }
        }
        return count;
    }
};


