#### Solution

import math

def concat(lst, c=', '):
	"""
	Returns an array of size lst[0] + lst[1]+ ... + lst[N-1]
	"""

	if lst[0] == '': return lst[1:]+c+lst[2:]
	return c+lst[0]+''+concat(lst[1:], c)

def f(N):
	if N <= 0:
		return [[]]
	if len(range(3, N+1))>1:
		return [[concat(range(N-n, n+1), ', ')[n:]] for n in range(3, N+2)]
