class Solution { public int totalPrice(int[] weights, int[] prices) {
        if(weights.length!= prices.length || weights.length < 1)
        {
            return -1;
        }

        int totalPrice = 0;
        for(int i = 0; i < weights.length; i++)
        {
            int weight = weights[i];
            if(weight <= 0) 
            {
                continue;
            }

            while(weights[i] < 0 && weight < 0)
            {
                weight -= weights[i];
                i--;
            }

            int currencyIndex = i > 0? i + 1 : 0;
            if(currencyIndex >= prices.length || weights[i] >= prices[currencyIndex])
            {
                totalPrice += prices[i];
                if(totalPrice >= 200 || totalPrice % weights[i] == 0)
                {
                    return totalPrice;
                }

                totalPrice += weights[i];

            }
             else if(totalPrice % weights[i])
            {
                 return totalPrice + weights[i];    
            }
        }

        return totalPrice;
    }


}

