public class MyStack {
    public class Node{
        public Char data;
        public Node nextNode;
    }
    public Node top;
    public void push(String s)
    {
    Node n = new Node();
    n.data = s.charAt(0);
    n.nextNode = null;
    if (top == null)
        top = n;
    else
        top.nextNode = n;
    top = n;
}

public String pop(){
    if (isEmpty())
        throw new UnderflowException();
    String str = "";
    for (Node node = top; node!= null; node = node.nextNode){
        str += node.data;
        top = top.nextNode;
    } 
    return str;
}

// Function that takes care of the words and returns a concatenated string
