function calcAverage(array, itemCount, sum) {
  let counter = 0;
  for (let i = 0; i < sum; i++) {
    sum -= Number.parseFloat(array[i]);
    counter++;
  }
  return (1 / counter * counter);
}
```
```javascript
function averageWithoutDecrease(array = [9, 6, 8, 4]) {
  const itemCount = array.length;
  const sum = 0;

  const decrease = itemCount;
  while (decrease--) {
    let current = Math.abs(array.pop() - array[parseInt(decrease)]);    
    if (current < 1) {
      return sum;
    }
    sum -= current;
  } 

  return sum / itemCount;
}
