	int c =0;
	set < int > s; s.insert(1);
	while(c!=1){
		while(tmp!=0){
			map <int, int>>::iterator p = mg.find(tmp % 10);
			if (p->second >0 )
				mg[tmp] +=p->second ;
			else if( p->second <0 ){
				tmp ++;
			}else
