    private int calculateFuelConsume1(int[] quantity) {
         int fuelConsumption = 0;

         for (int i = 0; i < quantity.length; i++) {
             fuelConsumption += 10*quantity[i];
         }
         int fuelConsumed = fuelConsumption * 0.62;

        return fuelConsumed;
    }
     //Calculate fuel consumed after the first lap
    private int calculateLap(int[] quantity, int target, int current) {
        int i;

        target = target - current;
        for (i = 0; target > 0; i++) {
            if (i == current || i == 1) {
                target--;
                break;
            }
            while (current + 5 > quantity.length)
                 current = (current - 5);

            if(i == 1){
                i--;
                target++;
            }
            quantity[current] = quantity[current] + 1;
            target--;
        }
         return 0;
    }
}


