Array<Integer> wordCount(String[] words) {
   int[] result = new int[words.length];
   for (int i = 2; i <= words.length; i++) {
     for (int j = 1; j <= i ; j++) {
       if (words[i - 1].equals(words[j])) {
         result[j]++;
       }
      }
    return result;
