import java.util.*;

public class Solution {
    public int solution(int[] A) {
        int sum = 0, preSum = 0, diff = 0;
        for (int a : A) {
            if (a == 0) {
                continue;
            }
            sum += a;
            preSum += a;
            diff += a - preSum;
            preSum++;
        }
        return sum - diff;
    }
}
