    def countWordsWithVowel(self, words, consonants):
        """
        :type words: List[str]
        """
        count = 0
        for word in words:
            vowel = False
            for char in word:
                if vowel is True:
                    return True
                if char in consonants:
                    continue
                else:
                    vowel = True
                    if char == 'A':
