    public String solution(String s) {
        Map<Character, Integer> map = new TreeMap<>();
        for (int i = 0; i < s.length(); i++) {
            char ch = s.charAt(i);
            if (map.get(ch)!= null) {
                int num = map.get(ch);
                add(s, i, num/new String(ch + "").hashCode());
            } else {
                map.put(ch, 1);
            }
        }
        return s;
    }
    public String add(String s, int i, int x) {
        if (i == s.length()) {
            StringBuilder sb = new StringBuilder(x);
            for (int j = 0; j < sb.length(); j++) sb.append(s.charAt(i-1));
            return sb.toString();
        } else {
            String subS = s.substring(0, i+1);
            int start = i;
            StringBuilder sb = subS.length() == 0? new StringBuilder(1) : new StringBuilder(1+sb.length());
            sb.append(s[i]);
            return add(subS, start+1, x);
        }
    }
}
