    // O(n) in terms of no. of keys
    static Long longSum(Long []l){
        if(l == null || l.length < 1 || l.length == 0){
            return 0L;
        }
        int noElements = l.length;
        int result = 0;
        for(int i=0; i < noElements; i++){
            result = result + l[i];
        }
        return result;
    }

