class Solution {
    static parse(answers:string):string[] {
        const ans = new Array();
        const ans0 = answers.replace(',', '').split(' ');
        const ans1 = answers.replace(',|:', '|:').replace(': ',':').split('|:');
        for (let i = 0; i < ans0.length; i++) {
            ans[i] = ans0[i];
        }
        ans1.forEach(a => {
            if (a!= '') {
                let c = a.split(' ');
                ans[ans.length+1] = c[0];
                ans[ans[ans.length-2]+1] = c[1];
            }
        })
        return ans;
    }
    static getAnswer(answers:string[], ans:number) {
        for (let an of answers) {
            let c = ans.toString(), ansA = c.split(' '); 
            if (ansA[0] == this.parse(answers)[ansA[1]]) {
                console.log(c);
                return c;
            }
        }
    }
    public static main(@RootParam() string[] answers:string[]) {
        let ans = this.parse(ansswers);
        ans.forEach(a => console.log(this.getAnswer(ans, a)));
    }
}
