    if (array == null || array.length == 0) {
        return -1;
    }
    // If the second element is too big or too small
    int secondBig = 0;
    int secondSmall = array.length - 1;
    // if secondBig is bigger than length which means it is a null element
    // if secondSmall is less than 0 than also it mean null, null will return -1
    while (secondBig >= 0 && secondSmall >= 0 && secondBig < secondSmall) {
        // If the difference between the two elements is bigger than two, it will return -1    
