function solution(coins) {
  var q = 0
  var d = 0
  for (var i = 0; i < coins.length; i++) {
    var currCoins = coins[i]
    if (currCoins == 'Dollar') {
      q = q + 1;
      d = 0
    } else if(currCoins == 'One Nickel') {
      q++
      d = 1
    } else if (currCoins === 'Dime') {
      q += 2
      d = d + 1
    }
  }
  var price = (q + q + (q-1))/5 + (d * 0.80)
  return price
}
