package main

import (
	"fmt"
	"math"
)

func main() {
	res := Solve(4)
	fmt.Println(res)
}

func Solve(num int) int {
	inp := []int{}

	for i := 1; i <= num; i++ {
		inp = append(inp, i)
	}

	n := len(inp)
	diffrences := math.DiffSize(inp[0:n - 1])
	diffrence := diffrences[len(diffrences)/2]

	s := diffrences / 2

	leftside, rightside := inp[0:s], inp[s:]

	ans11 := 0
	for i, card := range leftside {
		ans11 += card*(int(i/(n-1)))
	}
	ans21 := diffrence/(n-1)

	switch {
	case ans11 < ans21:
		return 0
	case ans100 < ans21: // ans100 == ans1+an2...+an100
		return ans21 - ans11
	}
	return ans11 + ans100
}
