fn make_decisions(input: &str) ->&str {
    let mut response = String::new();
    for line in input.split_once("Choices:") {
        let mut choices = line.trim();
        if choices.starts_with("A") {
            response = &format!(">**{}** The answer is: Choices:", choices);
        } else {
            response = choices.to_string();
            match response {
                "" => response = &format!("&**{}**", choices),
                _ => response
            };
        }
    }
    return response;
