    Stack<String> choices = new Stack<String>();

    while(true) {

      String line = buffer.readLine();
      if(line.matches(".*")) {
        break;
      }

      String[] split = line.split(" ");
      String choice = split[split.length - 1];
      choices.push(choice);
    }

    int index = choices.pop();
    System.out.println(response(index));

  }
}

