### Function:  
char main(void)
{
    char throw[10];
    char *p;
    int scores;

    do {
        printf("Enter your throw: ");
        scanf("%s", throw);
    } while (strlen(throw)!= 10);


    printf("The value of the segment you’ve hit is %.2f. You win %.2f from this segment.\n\n", 
       scores, (double)(scores * 1.5));

    strcpy(throw+1, throw+strlen(throw));
    strcat(throw, throw+strlen("000"));
    p=throw+strlen(throw)-2;

    while(*p==0) p++;
    if(*p=='+') *p='.';
