class Solution
{
    public:
    int divideTwoLists(vector<int> &lefts, vector<int> &rights) {
        if (lefts.size() < 1) return 0;
        if (rights.size() <1) return 0;;
        int r=0, leftSum=0,rightSum=0;
        int temp=0;
        for(auto leftIdx: lefts )
        {
            if(leftIdx>0)
            {
                for(auto rightIdx: rights)
                {
                    if(rightIdx>leftIdx)
                    {
                       temp = leftIdx;
                    }
                    else
                    {

                        r++;
                        leftSum++;
                    }
                }
            }
        }
        r=lefts.size()+rightSum;

        return r-leftSum;
    }
};
```

