/** * * @param {Number} x */
function calculateRemainder(x){
  // if remainder is 0, return x
  // else return ( remainder * 100 / divider + x ) % remainder
  let tmp_r,tmp_d,res;
  tmp_r = remainder(x,100) || 1;
  tmp_d = tmp_r % divider || 0;
  res=(tmp_r * 100)/divider + x % tmp_r;
  tmp_f = divider*tmp_d;
  return (res % tmp_r || 0) >> Math.min(0,Math.abs(tmp_f));
}
function remainder(x,y){
  let z = x%y,
  result = Math.floor(x/y);
  if (result === 0 || (z<0))
  return result;
  return result*y+z;
}

