public static int CalculateNumLeft(int totalIn, int used, int leftNeeded, int usedOnLeft){

    if (leftNeeded == 0){
        return -1;
    }

    int left = leftNeeded>totalIn? totalIn: leftNeeded;
    if (totalIn - used <= left){

        int leftover = totalIn - used;
        int leftWithoutUsed = getNumLeft(leftover, usedOnLeft);

        return (leftWithoutUsed>left? leftWithoutUsed: left);
    }


    return CalculateNumLeft (totalIn - used, used, leftNeeded - 1, leftOnLeft + usedOnLeft);
}
public static int getNumLeft(int all, int allUsed){
   return all > 0? getNumLeft(all - allUsed, allUsed) : 0;
}
