#include <stdio.h>
#include <sys/types.h>
#define _POSIX_C_SOURCE 199309L
#include <unistd.h>

int max(int *cmdArgs, int i)
{
    if ((i % 2) == 1 && cmdArgs[i] > cmdArgs[i - 1])
        return cmdArgs[i];
    else
        return cmdArgs[(i) - 1];
}

int main(int len, char *cmdArgs[])
{
    int maxPos = max(cmdArgs, 0);

    for (int i = 0; i <= maxPos; i++)
    {
        printf("max value from %d to %d\n", i, maxPos);
    }

