```javascript
var solve = function(a) {
    var result = [],
        resultSum = 0;
    for (var i=0; i < a.length; i += 1) {
        for (var ii=0; ii < a[i]; ii += 1) { // checks if each element from 0 to a[i] is prime
            if (a[i] % ii === 0) {
                resultSum += a[i];
                result.push(a[i]);
                break;
            }
        }
    }
    if (resultSum === 0) return [0,true]; // prime number array
    else return [resultSum,false];
}
