bool isPalindrome(string s)
{
   if (s == "" || s[0] == "" || s.length == 1) 
   {
     return false;
   }

   for(int i = 1; i!= s.length; ++i) 
   { 
     if (s[i]!= s[s.length - i]) 
     {
       return false;
     } 
   }
   return true;
}
